import React from 'react';

const Home = () => {
  const blogs = [
    {
      id: 1,
      image: "data:image/jpeg;base64,",
      title: "Advances in Quantum Computing",
      description: "How quantum computing is pushing the boundaries of technology and problem-solving."
    },
    {
      id: 2,
      image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR7V9FBNzQ_0ctZjJMK67tvLZkxAQe8_dxYiw&s",
      title: "The Rise of Electric Vehicles",
      description: "Exploring the rapid adoption of EVs and their impact on the environment."
    },
    {
      id: 3,
      image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTsKo1jE6dUxbymIW-GL_xPFX7zqJUn7yVCqA&s",
      title: "AI in Healthcare",
      description: "The transformative role of AI in diagnostics, treatment, and patient care."
    },
    {
      id: 4,
      image: "data:image/jpeg;base64,",
      title: "The Future of Renewable Energy",
      description: "Insights into renewable energy sources reshaping the global energy landscape."
    },
    {
      id: 5,
      image: "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQyoCH-T9pE3OVrZ3kgiBkzefMX4xUKBGDeeg&s",
      title: "Exploring Virtual Reality",
      description: "A look at VR's advancements and its potential across various sectors."
    },
    {
      id: 6,
      image: "data:image/jpeg;base64,",
      title: "Space Exploration: The Next Frontier",
      description: "The latest developments in space exploration and plans for future missions."
    },
  ];

  return (
    <div className="p-8">
      <h1 className="text-3xl font-bold mb-6">Recently Published Posts</h1>
      
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
        {blogs.map((blog) => (
          <div key={blog.id} className="bg-white shadow-md rounded-lg overflow-hidden">
            <img
              src={blog.image}
              alt={blog.title}
              className="w-full h-48 object-cover"
            />
            <div className="p-6">
              <h2 className="text-xl font-semibold mb-2">{blog.title}</h2>
              <p className="text-gray-700">{blog.description}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Home;
